  #{unionId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP},
  #{email,jdbcType=VARCHAR},#{remindHour,jdbcType=INTEGER},#{remindMinute,jdbcType=INTEGER}
